mapper.update:
update student


        name = #{name},


        gender = #{gender},


        grade = #{grade},


        clazz = #{clazz},


        admission_date = #{admissionDate},


        head_teacher_name = #{headTeacherName}

    update_time = #{updateTime}

where id = #{id}

mapper.select:



        name = #{name}


        and gender = #{gender}


        and grade = #{grade}


        and clazz = #{clazz}


        and admission_date between #{begin} and #{end}


        and admission_date <= #{end}


        and admission_date >= #{begin}


order by update_time desc
